mapper.insert:
INSERT INTO tbl_files (
		FILE_SEQ,
		FILE_P_CODE,
		FILE_ORIGIN_NAME,
		FILE_UPLOAD_NAME
) VALUES(
		#{p_code},
		#{file_origin_name},
		#{file_upload_name}
)

mapper.select:
SELECT SEQ_FILES.NEXTVAL, SUB.* FROM (
SELECT #{file.file_p_code}, #{file.file_origin_name}, #{file.file_upload_name} FROM DUAL
) SUB
